
class Background
{
    constructor()
    {
        window.addEventListener('load', e=>{
            this._el = _('.body-background')[0];
            window.addEventListener('resize', this.update.bind(this));
            window.addEventListener('scroll', this.update.bind(this));
            this.update();
        });
    }

    update()
    {
        /*
        if(!this._timeout)
        {
            this._timeout = setTimeout(()=>{
                this.setPosition(this.computePosition()/1.5)
                this._timeout = null;
            }, 1);
        }
        */
        this.setPosition(-this.computePosition()/4)
    }

    computePosition()
    {
        return window.scrollY;
    }

    setPosition(position)
    {
        this._el.style.transform = 'translateZ(0) translateY(' + position + 'px)';
    }
}

let background = new Background();


function _(s)
{
    return document.querySelectorAll(s);
}

function uuid()
{
    const g = ()=> 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });

    let uuid;
    do
    {
        uuid=g();
    } while(document.getElementById(uuid));
    return uuid;
}

class Tv
{
    constructor()
    {
        window.addEventListener('load', e=>{
            _('.module-tv .tv').forEach(tv=>{
                let id = uuid();
                tv.id = id;
                new Twitch.Embed(id, {
                    width: '100%',
                    height: '100%',
                    channel: 'azimmov',
                    layout : 'video'
                });

            })
        });
    }
}

let tv = new Tv();
